import React, { useState } from 'react';
import axios from 'axios';
import { useTranslation } from 'react-i18next';
import '../styles/PredictionForm.css';

const PredictionForm = () => {
  const [formData, setFormData] = useState({
    district: '',
    month: '',
    day: '',
    year: ''
  });
  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState('');
  const { t } = useTranslation();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setPrediction(null);
    try {
      const response = await axios.post('http://localhost:5000/api/predict', formData);
      setPrediction(response.data.prediction);
    } catch (err) {
      setError(t('predictionError'));
      console.error(err);
    }
  };

  return (
    <div className="prediction-form-container">
      <h2>{t('predictionTitle')}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="district">{t('districtLabel')}</label>
          <input
            type="text"
            id="district"
            name="district"
            value={formData.district}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="month">{t('monthLabel')}</label>
          <input
            type="number"
            id="month"
            name="month"
            value={formData.month}
            onChange={handleChange}
            required
            min="1"
            max="12"
          />
        </div>
        <div className="form-group">
          <label htmlFor="day">{t('dayLabel')}</label>
          <input
            type="number"
            id="day"
            name="day"
            value={formData.day}
            onChange={handleChange}
            required
            min="1"
            max="31"
          />
        </div>
        <div className="form-group">
          <label htmlFor="year">{t('yearLabel')}</label>
          <input
            type="number"
            id="year"
            name="year"
            value={formData.year}
            onChange={handleChange}
            required
            min="2024"
          />
        </div>
        <button type="submit">{t('predictButton')}</button>
      </form>
      {prediction !== null && (
        <div className="prediction-result">
          <h3>{t('predictionResultLabel')}:</h3>
          <p>{t('waterLevelPrediction', { prediction: prediction.toFixed(2) })}</p>
        </div>
      )}
      {error && <p className="error-message">{error}</p>}
    </div>
  );
};

export default PredictionForm;
